int sum_of_digits(int num) {
    if (num == 0) return 0;
    int sum = 0;
    bool is_negative = num < 0;

    // Convert number to string for easy digit extraction
    std::string str_num = std::to_string(num);

    // Handle the first signed digit separately
    if (is_negative) {
        sum -= abs(str_num[1] - '0');  // Subtract the absolute value of the first digit
    }

    // Sum up remaining digits
    for (size_t i = is_negative ? 1 : 0; i < str_num.size(); ++i) {
        if (!is_negative || i > 0) {
            sum += str_num[i] - '0';
        }
    }

    return sum;
}

int count_nums(std::vector<int> n) {
    int count = 0;

    for (const auto& number : n) {
        if (sum_of_digits(number) > 0) {
            count++;
        }
    }

    return count;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (count_nums({}) == 0);
    assert (count_nums({-1, -2, 0}) == 0);
    assert (count_nums({1, 1, 2, -2, 3, 4, 5}) == 6);
    assert (count_nums({1, 6, 9, -6, 0, 1, 5}) == 5);
    assert (count_nums({1, 100, 98, -7, 1, -1}) == 4);
    assert (count_nums({12, 23, 34, -45, -56, 0}) == 5);
    assert (count_nums({-0, 1}) == 1);
    assert (count_nums({1}) == 1);
}

