bool move_one_ball(const std::vector<int>& arr) {
    if (arr.empty()) {
        return true;
    }

    int n = arr.size();

    // Check each possible starting point for a right shift operation
    for (int i = 0; i < n; ++i) {
        bool is_sorted = true;

        // Check if the array is sorted after performing a right shift starting from index i
        for (int j = 1; j < n; ++j) {
            int prev_index = (i + j - 1) % n;
            int current_index = (i + j) % n;

            // If any element violates the non-decreasing order, set is_sorted to false
            if (arr[prev_index] > arr[current_index]) {
                is_sorted = false;
                break;
            }
        }

        // If the array is sorted after a right shift starting from index i, return true
        if (is_sorted) {
            return true;
        }
    }

    // If no such starting point exists that results in a sorted array, return false
    return false;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (move_one_ball({3, 4, 5, 1, 2})==true);
    assert (move_one_ball({3, 5, 10, 1, 2})==true);
    assert (move_one_ball({4, 3, 1, 2})==false);
    assert (move_one_ball({3, 5, 4, 1, 2})==false);
    assert (move_one_ball({})==true);
}

