    {1, "One"}, {2, "Two"}, {3, "Three"},
    {4, "Four"}, {5, "Five"}, {6, "Six"},
    {7, "Seven"}, {8, "Eight"}, {9, "Nine"}
};

std::vector<std::string> by_length(std::vector<int> arr) {
    // Filter and sort the array
    std::vector<int> filtered_sorted;
    for (int num : arr) {
        if (num >= 1 && num <= 9) {
            filtered_sorted.push_back(num);
        }
    }

    std::sort(filtered_sorted.begin(), filtered_sorted.end());

    // Reverse the sorted vector
    std::reverse(filtered_sorted.begin(), filtered_sorted.end());

    // Convert digits to their corresponding names
    std::vector<std::string> result;
    for (int num : filtered_sorted) {
        if (digit_names.find(num) != digit_names.end()) {
            result.push_back(digit_names[num]);
        }
    }

    return result;
}

// Example usage and check function
void check() {
    std::vector<int> arr1 = {2, 1, 1, 4, 5, 8, 2, 3};
    auto result1 = by_length(arr1);
    for (const auto& str : result1) {
        printf("%s ", str.c_str());
    }
    printf("\n");

    std::vector<int> arr2 = {};
    auto result2 = by_length(arr2);
    if (result2.empty()) {
        printf("Empty vector\n");
    }

    std::vector<int> arr3 = {1, -1, 55};
    auto result3 = by_length(arr3);
    for (const auto& str : result3) {
        printf("%s ", str.c_str());
    }
    printf("\n");
}


