using namespace std;

// Function to count odd numbers in the vector
int countOdd(const vector<int>& lst) {
    int count = 0;
    for (const auto& num : lst) {
        if (num % 2 != 0) {
            ++count;
        }
    }
    return count;
}

// Function to check if there are enough even numbers in the second list
bool hasEnoughEvens(const vector<int>& lst, int requiredCount) {
    int evens = 0;
    for (const auto& num : lst) {
        if (num % 2 == 0) {
            ++evens;
        }
        if (evens >= requiredCount) {
            return true;
        }
    }
    return false;
}

string exchange(vector<int> lst1, vector<int> lst2) {
    int oddCount = countOdd(lst1);

    // If there are no odd numbers in lst1, it's already possible
    if (oddCount == 0) {
        return "YES";
    }

    // Check if lst2 has enough even numbers to replace all odd numbers in lst1
    if (hasEnoughEvens(lst2, oddCount)) {
        return "YES";
    } else {
        return "NO";
    }
}

