// Function to find the histogram of a given string
map<char, int> histogram(string test) {
    // Map to store character counts
    map<char, int> charCount;

    // Count occurrences of each character
    for (char c : test) {
        if (c != ' ') {  // Skip spaces
            charCount[c]++;
        }
    }

    // Find the maximum count among all characters
    int maxCount = 0;
    for (auto& entry : charCount) {
        if (entry.second > maxCount) {
            maxCount = entry.second;
        }
    }

    // Filter and return only those characters with the maximum count
    map<char, int> result;
    for (auto& entry : charCount) {
        if (entry.second == maxCount) {
            result[entry.first] = entry.second;
        }
    }

    return result;
}

// Function to print the histogram map
void printHistogram(const map<char, int>& hist) {
    for (const auto& entry : hist) {
        cout << "{" << entry.first << ", " << entry.second << "} ";
    }
    cout << endl;
}


