long long minSubArraySum(std::vector<long long> nums) {
    if (nums.empty()) return 0; // Handle empty vector case

    long long min_sum = nums[0];
    long long current_min = nums[0];

    for (size_t i = 1; i < nums.size(); ++i) {
        current_min = std::min(nums[i], current_min + nums[i]);
        min_sum = std::min(min_sum, current_min);
    }

    return min_sum;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (minSubArraySum({2, 3, 4, 1, 2, 4}) == 1);
    assert (minSubArraySum({-1, -2, -3}) == -6);
    assert (minSubArraySum({-1, -2, -3, 2, -10}) == -14);
    assert (minSubArraySum({-9999999999999999}) == -9999999999999999);
    assert (minSubArraySum({0, 10, 20, 1000000}) == 0);
    assert (minSubArraySum({-1, -2, -3, 10, -5}) == -6);
