int max_fill(std::vector<std::vector<int>> grid, int capacity) {
    int total_operations = 0;

    // Iterate through each well (row)
    for(const auto& row : grid) {
        int water_units = 0; // Total units of water in the current well

        // Sum up all 1s in the current well
        for(int unit : row) {
            water_units += unit;
        }

        // Calculate how many times a bucket needs to be lowered for this well
        total_operations += std::ceil(static_cast<double>(water_units) / capacity);
    }

    return total_operations;
}


