// Helper function to check if a character is a vowel (case sensitive)
bool is_vowel(char c) {
    string vowels = "aeiouAEIOU";
    return vowels.find(c) != string::npos;
}

// Function to get the closest vowel that stands between two consonants from the right
string get_closest_vowel(string word) {
    int n = word.length();

    // Iterate from second last character to first character
    for (int i = n - 2; i > 0; --i) {
        char left_char = word[i-1];
        char middle_char = word[i];
        char right_char = word[i+1];

        // Check if the current character is a vowel and both surrounding characters are consonants
        if (is_vowel(middle_char) && !is_vowel(left_char) && !is_vowel(right_char)) {
            return string(1, middle_char);  // Return the vowel as a single-character string
        }
    }

    // If no such vowel is found, return an empty string
    return "";
}


