bool is_consonant(char c) {
    // Convert character to lowercase for case-insensitive comparison.
    char lower_c = tolower(c);
    return (lower_c >= 'a' && lower_c <= 'z') && !(lower_c == 'a' || lower_c == 'e' || lower_c == 'i' || lower_c == 'o' || lower_c == 'u');
}

vector<string> select_words(string s, int n) {
    vector<string> result;

    // Split the string into words
    size_t start = 0;
    size_t end = s.find(' ');
    while (end != string::npos) {
        string word = s.substr(start, end - start);

        // Count consonants in the current word
        int consonant_count = count_if(word.begin(), word.end(), is_consonant);

        // If the number of consonants matches n, add to result vector
        if (consonant_count == n) {
            result.push_back(word);
        }

        start = end + 1;
        end = s.find(' ', start);
    }

    // Check for the last word in the string
    string last_word = s.substr(start);
    int consonant_count_last = count_if(last_word.begin(), last_word.end(), is_consonant);

    if (consonant_count_last == n) {
        result.push_back(last_word);
    }

    return result;
}


