// Helper function to count the number of ones in the binary representation of an integer.
int count_ones(int n) {
    if (n >= 0) {
        return __builtin_popcount(n);
    } else {
        // For negative numbers, use two's complement and count the ones
        unsigned int positive_n = -(n + 1);
        return __builtin_popcount(positive_n);
    }
}

// Main function to sort the array based on the number of ones in their binary representation.
std::vector<int> sort_array(std::vector<int> arr) {
    // Sort using a custom comparator
    std::sort(arr.begin(), arr.end(), [](int a, int b) {
        int count_a = count_ones(a);
        int count_b = count_ones(b);

        if (count_a == count_b) {
            return a < b;
        }
        return count_a < count_b;
    });

    return arr;
}


