int solution(vector<int> lst) {
    int sum = 0;

    // Iterate through the vector
    for (size_t i = 0; i < lst.size(); ++i) {
        // Check if index is even and value at that index is odd
        if ((i % 2 == 0) && (lst[i] % 2 != 0)) {
            sum += lst[i];
        }
    }

    return sum;
}


