bool valid_date(string date) {
    // Check for empty string
    if (date.empty()) return false;

    // Find positions of '-' characters to split the date into components
    size_t pos1 = date.find('-');
    size_t pos2 = date.find('-', pos1 + 1);

    // Ensure there are exactly two '-' in the correct format
    if (pos1 == string::npos || pos2 == string::npos) return false;

    // Extract month, day, and year from the string
    int month = stoi(date.substr(0, pos1));
    int day = stoi(date.substr(pos1 + 1, pos2 - pos1 - 1));
    int year = stoi(date.substr(pos2 + 1));

    // Validate month range
    if (month < 1 || month > 12) return false;

    // Validate day range based on the month
    if ((month == 4 || month == 6 || month == 9 || month == 11) && (day < 1 || day > 30)) {
        return false;
    } else if ((month == 2 && (day < 1 || day > 29))) {
        return false;
    } else if ((day < 1 || day > 31)) {
        return false;
    }

    // If all checks pass, the date is valid
    return true;
}


