vector<int> get_odd_collatz(int n) {
    vector<int> odds;

    while (n != 1) {
        if (n % 2 == 1) { // Check if the number is odd
            odds.push_back(n);
        }

        // Generate next term in Collatz sequence
        if (n % 2 == 0) {
            n /= 2;
        } else {
            n = 3 * n + 1;
        }
    }

    // Add the last number, which is always 1 and odd
    odds.push_back(1);

    // Sort the vector of odd numbers in increasing order
    sort(odds.begin(), odds.end());

    return odds;
}

