// Function to check if a character has an odd order in the alphabet
bool is_odd_order(char c) {
    // Convert lowercase letter to its position in the alphabet (0 for 'a', 1 for 'b', etc.)
    int pos = c - 'a';
    return pos % 2 == 1;
}

// Function to count the number of characters with odd order
int count_odd_order(const string& str) {
    int count = 0;
    for (char c : str) {
        if (islower(c) && is_odd_order(c)) {
            ++count;
        }
    }
    return count;
}

vector<string> split_words(string txt) {
    vector<string> result;

    // Check for whitespace
    size_t space_pos = txt.find(' ');
    if (space_pos != string::npos) {
        // Split on whitespace
        size_t start = 0, end;
        while ((end = txt.find(' ', start)) != string::npos) {
            result.push_back(txt.substr(start, end - start));
            start = end + 1;
        }
        result.push_back(txt.substr(start)); // Add the last word
    } else {
        // Check for commas if no whitespace was found
        size_t comma_pos = txt.find(',');
        if (comma_pos != string::npos) {
            // Split on commas
            size_t start = 0, end;
            while ((end = txt.find(',', start)) != string::npos) {
                result.push_back(txt.substr(start, end - start));
                start = end + 1;
            }
            result.push_back(txt.substr(start)); // Add the last part
        } else {
            // No whitespace or commas found, count odd order letters
            int count = count_odd_order(txt);
            result.push_back(to_string(count));
        }
    }

    return result;
}


