bool is_sorted(vector<int> lst){
    // Check if list is empty or has only one element, return true as it's trivially sorted.
    if (lst.size() <= 1) {
        return true;
    }

    map<int, int> countMap; // To keep track of the frequency of each number

    for(int i = 0; i < lst.size(); ++i){
        // Check ascending order
        if(i > 0 && lst[i] < lst[i-1]) {
            return false;
        }

        // Count occurrences of each element
        countMap[lst[i]]++;

        // If any number appears more than twice, return false.
        if(countMap[lst[i]] > 2) {
            return false;
        }
    }

    return true;    
}

// Example usage and check function (not part of the solution but for testing)
void check() {
    assert(is_sorted({5}) == true);
    assert(is_sorted({1, 2, 3, 4, 5}) == true);
    assert(is_sorted({1, 3, 2, 4, 5}) == false);
    assert(is_sorted({1, 2, 3, 4, 5, 6}) == true);
    assert(is_sorted({1, 2, 2, 3, 3, 4}) == true);
    assert(is_sorted({1, 2, 2, 2, 3, 4}) == false);
}


