int prod_signs(std::vector<int> arr) {
    if (arr.empty()) return -32768;

    int sum_magnitude = 0;
    int product_of_signs = 1;

    for (const auto& num : arr) {
        // Calculate the magnitude
        sum_magnitude += std::abs(num);

        // Determine the sign and update the product of signs
        if (num == 0) return 0; // Any number multiplied by zero is zero
        else if (num > 0) continue; // Positive numbers don't change the product_of_signs
        else product_of_signs *= -1; // Negative numbers flip the sign

    }

    // Return the final result: sum of magnitudes * product of signs
    return sum_magnitude * product_of_signs;
}

