std::vector<int> tri(int n) {
    if (n == 0) {
        return {1};
    }

    std::vector<int> sequence = {1, 3};  // Initial values for tribonacci sequence

    for (int i = 2; i <= n; ++i) {
        int next_value;
        if (i % 2 == 0) {  // If i is even
            next_value = 1 + (i / 2);
        } else {  // If i is odd
            next_value = sequence[i - 1] + sequence[i - 2];
            // The value at index i+1 will be computed in the subsequent iteration.
            if (i < n) {
                sequence.push_back(next_value);  // Store the current value for future use
            }
        }

        if (i <= n) {
            sequence.push_back(next_value);
        }
    }

    return sequence;
}

