using namespace std;

struct Path {
    vector<int> values;
    int x, y;

    // Constructor to accept an rvalue or initializer list
    Path(vector<int> v, int _x, int _y) : values(std::move(v)), x(_x), y(_y) {}

    // Comparison operator for priority queue
    bool operator>(const Path& other) const {
        return values.back() > other.values.back();
    }
};

vector<int> minPath(const vector<vector<int>>& grid, int k) {
    vector<int> result;
    if (grid.empty() || grid[0].empty()) return result;

    priority_queue<Path, vector<Path>, greater<Path>> pq;

    // Initialize the priority queue with starting positions
    for (int i = 0; i < grid.size(); ++i) {
        for (int j = 0; j < grid[i].size(); ++j) {
            pq.push(Path({grid[i][j]}, i, j));
        }
    }

    while (!pq.empty() && result.size() < k) {
        Path currentPath = pq.top();
        pq.pop();

        if (result.size() == 0 || result.back() != currentPath.values.back()) {
            result.push_back(currentPath.values.back());
        }

        // Add next elements to the priority queue
        int x = currentPath.x, y = currentPath.y;
        if (x + 1 < grid.size()) pq.push(Path(std::move(currentPath.values), x + 1, y));
        if (y + 1 < grid[x].size()) pq.push(Path(std::move(currentPath.values), x, y + 1));

        // Remove duplicates
        while (!pq.empty() && result.back() == pq.top().values.back()) {
            pq.pop();
        }
    }

    return result;
}


