vector<int> largest_smallest_integers(vector<int> lst) {
    int max_negative = INT_MIN; // Initialize to the smallest possible integer
    int min_positive = INT_MAX; // Initialize to the largest possible integer

    bool found_negative = false;
    bool found_positive = false;

    for (int num : lst) {
        if (num < 0 && num > max_negative) {
            max_negative = num;
            found_negative = true;
        } else if (num > 0 && num < min_positive) {
            min_positive = num;
            found_positive = true;
        }
    }

    // If no negative or positive integers were found, set them to 0
    int largest_negative = found_negative ? max_negative : 0;
    int smallest_positive = found_positive ? min_positive : 0;

    return {largest_negative, smallest_positive};
}

