bool is_nested(string str) {
    stack<char> brackets;
    bool has_nested = false;

    for (char bracket : str) {
        if (bracket == '[') {
            brackets.push(bracket);
        } else if (bracket == ']') {
            if (!brackets.empty() && !has_nested && brackets.size() > 1) {
                has_nested = true;
            }
            if (!brackets.empty()) {
                brackets.pop();
            } else {
                // If there's a closing bracket without an opening one, return false
                return false;
            }
        }
    }

    // Check if there is at least one nested bracket and all brackets are matched
    return has_nested && brackets.empty();
}


