// Function to compute factorial of a number
long long factorial(int n) {
    if (n == 0 || n == 1)
        return 1;
    return n * factorial(n - 1);
}

// Function to compute the Brazilian factorial as defined in the problem statement
long long special_factorial(int n) {
    long long result = 1;
    for (int i = 1; i <= n; ++i) {
        result *= factorial(i); // Multiply the current factorial value with the result
    }
    return result;
}

// Function to test the correctness of the special_factorial function
