// Function to convert a string with decimal separator '.' or ',' to float
float string_to_float(const string& str) {
    size_t pos = str.find(',');
    if (pos != string::npos) {
        return stof(str.substr(0, pos) + "." + str.substr(pos + 1));
    }
    return stof(str);
}

// Function to compare two values of any type and return the larger one or "None" if equal
any compare_one(any a, any b) {
    // Convert strings to floats if necessary
    float fa = 0.0f;
    float fb = 0.0f;

    if (a.type() == typeid(string)) {
        fa = string_to_float(any_cast<string>(a));
    } else if (a.type() == typeid(int)) {
        fa = any_cast<int>(a);
    } else if (a.type() == typeid(float)) {
        fa = any_cast<float>(a);
    }

    if (b.type() == typeid(string)) {
        fb = string_to_float(any_cast<string>(b));
    } else if (b.type() == typeid(int)) {
        fb = any_cast<int>(b);
    } else if (b.type() == typeid(float)) {
        fb = any_cast<float>(b);
    }

    // Compare the values
    if (fa > fb) {
        return a;
    } else if (fb > fa) {
        return b;
    } else {
        return "None";
    }
}


