int sum_squares(const std::vector<int>& lst) {
    int total_sum = 0;

    for (size_t i = 0; i < lst.size(); ++i) {
        if (i % 3 == 0) {
            total_sum += lst[i] * lst[i]; // Square the value
        } else if (i % 4 == 0 && i % 3 != 0) {
            total_sum += lst[i] * lst[i] * lst[i]; // Cube the value
        }
    }

    return total_sum;
}


