// Function to parse a string fraction into numerator and denominator integers
void parseFraction(const string& fraction, int& num, int& den) {
    size_t pos = fraction.find('/');
    num = stoi(fraction.substr(0, pos));
    den = stoi(fraction.substr(pos + 1));
}

// Function to compute the greatest common divisor (GCD)
int gcd(int a, int b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

bool simplify(string x, string n) {
    // Parse fractions
    int numX, denX;
    parseFraction(x, numX, denX);

    int numN, denN;
    parseFraction(n, numN, denN);

    // Compute the product of the two fractions
    int prodNum = numX * numN;  // Numerator of the result
    int prodDen = denX * denN;  // Denominator of the result

    // Simplify the fraction by dividing numerator and denominator with their GCD
    int commonDivisor = gcd(prodNum, prodDen);
    prodNum /= commonDivisor;
    prodDen /= commonDivisor;

    // Check if the resulting fraction is a whole number (denominator should be 1)
    return prodDen == 1;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (simplify("1/5", "5/1") == true);
    assert (simplify("1/6", "2/1") == false);
    assert (simplify("5/1", "3/1") == true);
    assert (simplify("7/10", "10/2") == false);
    assert (simplify("2/10", "50/10") == true);
    assert (simplify("7/2", "4/2") == true);
    assert (simplify("11/6", "6/1") == true);
    assert (simplify("2/3", "5/2") == false);
    assert (simplify("5/2", "3/5") == false);
    assert (simplify("2/4", "8/4") == true);
    assert (simplify("2/4", "4/2") == true);
    assert (simplify("1/5", "5/1") == true);
    assert (simplify("1/5", "1/5") == false);
}

