int get_max_triples(int n) {
    vector<int> a(n);

    // Generate the array a based on the formula i * i - i + 1
    for (int i = 1; i <= n; ++i) {
        a[i-1] = i * i - i + 1;
    }

    int count = 0;

    // Count triples (a[i], a[j], a[k]) where i < j < k and their sum is divisible by 3
    for (int i = 0; i < n-2; ++i) {
        for (int j = i+1; j < n-1; ++j) {
            for (int k = j+1; k < n; ++k) {
                if ((a[i] + a[j] + a[k]) % 3 == 0) {
                    count++;
                }
            }
        }
    }

    return count;
}


