bool isFirstAndLastDigitOdd(int num) {
    // Convert number to string for easy digit access
    if (num == 0) return false; // Handle zero case separately

    string strNum = to_string(abs(num));

    char firstDigit = strNum[0];
    char lastDigit = strNum[strNum.length() - 1];

    bool isFirstOdd = (firstDigit == '1' || firstDigit == '3' || 
                       firstDigit == '5' || firstDigit == '7' ||
                       firstDigit == '9');

    bool isLastOdd = (lastDigit == '1' || lastDigit == '3' || 
                      lastDigit == '5' || lastDigit == '7' ||
                      lastDigit == '9');

    return isFirstOdd && isLastOdd;
}

int specialFilter(vector<int> nums) {
    int count = 0;
    for (auto num : nums) {
        if (num > 10 && isFirstAndLastDigitOdd(num)) {
            ++count;
        }
    }
    return count;
}

