vector<string> bf(string planet1, string planet2) {
    // Define the order of planets from closest to farthest from the Sun.
    vector<string> planets = {"Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"};

    int index1, index2;
    bool valid_planet1 = false, valid_planet2 = false;

    // Find the indices of planet1 and planet2 in the planets vector.
    for (int i = 0; i < planets.size(); ++i) {
        if (planets[i] == planet1) {
            index1 = i;
            valid_planet1 = true;
        }
        if (planets[i] == planet2) {
            index2 = i;
            valid_planet2 = true;
        }

        // If both planets are found, break the loop.
        if (valid_planet1 && valid_planet2)
            break;
    }

    // Return an empty vector if either planet is not a valid name.
    if (!valid_planet1 || !valid_planet2) {
        return {};
    }

    // Ensure index1 < index2 for correct order of planets between them.
    if (index1 > index2) {
        swap(index1, index2);
    }

    vector<string> result;

    // Collect all planets that are located between planet1 and planet2.
    for (int i = index1 + 1; i < index2; ++i) {
        result.push_back(planets[i]);
    }

    return result;
}

