    vector<string> result;

    // Test case 1: Jupiter and Neptune
    result = bf("Jupiter", "Neptune");
    for (const auto& planet : result) {
        cout << planet << " ";
    }
    cout << endl; // Output should be: Saturn Uranus

    // Test case 2: Earth and Mercury
    result = bf("Earth", "Mercury");
    for (const auto& planet : result) {
        cout << planet << " ";
    }
    cout << endl; // Output should be: Venus

    // Test case 3: Mercury and Uranus
    result = bf("Mercury", "Uranus");
    for (const auto& planet : result) {
        cout << planet << " ";
    }
    cout << endl; // Output should be: Venus Earth Mars Jupiter Saturn
}


