// Helper function to calculate the sum of digits of an integer.
int sum_of_digits(int num) {
    int digit_sum = 0;
    bool is_negative = (num < 0);

    while (num != 0) {
        digit_sum += abs(num % 10);
        num /= -10; // Adjust for negative numbers
    }

    return digit_sum;
}

// Custom comparator function to sort based on sum of digits and original index.
bool custom_compare(const std::pair<int, int>& a, const std::pair<int, int>& b) {
    if (a.first == b.first) {
        return a.second < b.second; // Sort by original index
    }
    return a.first < b.first; // Sort by sum of digits
}

// Main function to sort the vector based on the criteria.
std::vector<int> order_by_points(std::vector<int> nums) {
    int n = nums.size();

    // Pair each number with its sum of digits and original index.
    std::vector<std::pair<int, int>> paired_nums;
    for (int i = 0; i < n; ++i) {
        paired_nums.push_back({sum_of_digits(nums[i]), i});
    }

    // Sort the pairs based on custom comparator.
    std::sort(paired_nums.begin(), paired_nums.end(), custom_compare);

    // Extract and return only the numbers from the sorted pairs.
    std::vector<int> result;
    for (const auto& p : paired_nums) {
        result.push_back(nums[p.second]);
    }

    return result;
}


