// Function to calculate the strength of an extension based on its name.
int calculateStrength(const string& extension) {
    int uppercase = 0;
    int lowercase = 0;

    for (char c : extension) {
        if (isupper(c)) {
            uppercase++;
        } else if (islower(c)) {
            lowercase++;
        }
    }

    return uppercase - lowercase;
}

// Function to find the strongest extension and return it in the required format.
string Strongest_Extension(string class_name, vector<string> extensions) {
    int max_strength = INT_MIN; // Initialize with minimum integer value
    string strongest_extension;

    for (const auto& extension : extensions) {
        int strength = calculateStrength(extension);

        if (strength > max_strength || (strength == max_strength && extensions.size() == 1)) {
            max_strength = strength;
            strongest_extension = extension;
        }
    }

    return class_name + "." + strongest_extension;
}


