std::vector<int> even_odd_count(int num) {
    // Convert the number to a string to easily iterate over each digit
    std::string str_num = std::to_string(std::abs(num));

    // Initialize counters for even and odd digits
    int even_count = 0;
    int odd_count = 0;

    // Iterate through each character in the string representation of the number
    for (char c : str_num) {
        int digit = c - '0';  // Convert char to integer

        // Check if the digit is even or odd and update the respective counter
        if (digit % 2 == 0) {
            even_count += 1;
        } else {
            odd_count += 1;
        }
    }

    return {even_count, odd_count};
}

