// Function to convert integer to Roman numeral in lowercase
string int_to_mini_roman(int number) {
    vector<pair<int, string>> roman_numerals = {
        {1000, "m"},  {900, "cm"}, {500, "d"},  {400, "cd"},
        {100, "c"},   {90, "xc"},  {50, "l"},   {40, "xl"},
        {10, "x"},    {9, "ix"},   {5, "v"},    {4, "iv"},
        {1, "i"}
    };

    string result = "";

    // Iterate through the roman_numerals vector
    for (const auto& numeral : roman_numerals) {
        while (number >= numeral.first) {
            result += numeral.second;
            number -= numeral.first;
        }
    }

    return result;
}

