vector<int> eat(int number, int need, int remaining) {
    // Calculate total number of carrots to eat after meals
    int totalToEat = number + need;

    // If there are enough remaining carrots, calculate the new counts
    if (remaining >= need) {
        return {totalToEat, remaining - need};
    }
    // Otherwise, eat all remaining carrots and be still hungry
    else {
        return {number + remaining, 0};
    }
}


