std::string find_max(std::vector<std::string> words) {
    if (words.empty()) return "";  // Handle empty vector case

    auto max_unique_chars = 0;
    std::string result;

    for (const auto& word : words) {
        std::set<char> unique_chars(word.begin(), word.end());

        int current_unique_count = unique_chars.size();

        if (current_unique_count > max_unique_chars || 
            (current_unique_count == max_unique_chars && word < result)) {
            max_unique_chars = current_unique_count;
            result = word;
        }
    }

    return result;
}


