using namespace std;

bool isLetter(char c) {
    return (c >= 'a' && c <= 'z') || (c >= 'A' && c <= 'Z');
}

char reverseCase(char c) {
    if (islower(c)) return toupper(c);
    else if (isupper(c)) return tolower(c);
    return c;
}

string solve(string s) {
    bool hasLetters = false;

    // Check if the string contains any letters
    for (char c : s) {
        if (isLetter(c)) {
            hasLetters = true;
            break;
        }
    }

    if (!hasLetters) {
        // If no letters, reverse the entire string
        return string(s.rbegin(), s.rend());
    } else {
        // Otherwise, transform each character as needed
        for (char &c : s) {
            if (isLetter(c)) c = reverseCase(c);
        }
        return s;
    }
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (solve("AsDf") == "aSdF");
    assert (solve("1234") == "4321");
    assert (solve("ab") == "AB");
    assert (solve("#a@C") == "#A@c");
    assert (solve("#AsdfW^45") == "#aSDFw^45");
    assert (solve("#6@2") == "2@6#");
    assert (solve("#$a^D") == "#$A^d");
    assert (solve("#ccc") == "#CCC");
}

