bool right_angle_triangle(float a, float b, float c) {
    // Ensure 'c' is the longest side (hypotenuse)
    float sides[3] = {a, b, c};
    std::sort(sides, sides + 3); // Sort the array in ascending order
    float hypotenuse = sides[2]; // The largest value will be at index 2

    // Check if it's a right-angled triangle using Pythagorean theorem
    return (sides[0] * sides[0] + sides[1] * sides[1]) == (hypotenuse * hypotenuse);
}


