using namespace std;

#include <vector>
#include <string>
#include <stack>

int do_algebra(std::vector<std::string> operators, std::vector<int> operands) {
    // Initialize a stack to handle the evaluation of the algebraic expression
    std::stack<long long> eval_stack;

    // Push the first operand onto the stack
    eval_stack.push(operands[0]);

    for (size_t i = 0; i < operators.size(); ++i) {
        const std::string& operator_str = operators[i];
        int next_operand = operands[i + 1];

        if (!eval_stack.empty()) {
            long long prev_operand = eval_stack.top();
            eval_stack.pop();

            // Perform the operation and push the result back onto the stack
            if (operator_str == "+") {
                eval_stack.push(prev_operand + next_operand);
            } else if (operator_str == "-") {
                eval_stack.push(prev_operand - next_operand);
            } else if (operator_str == "*") {
                eval_stack.push(prev_operand * next_operand);
            } else if (operator_str == "//") {
                eval_stack.push(prev_operand / next_operand); // Note: This is integer division
            } else if (operator_str == "**") {
                long long result = 1;
                for (int j = 0; j < next_operand; ++j) {
                    result *= prev_operand;
                }
                eval_stack.push(result);
            }
        }
    }

    // The final result is the top of the stack
    return static_cast<int>(eval_stack.top());
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (do_algebra({"**", "*", "+"}, {2, 3, 4, 5}) == 37);
    assert (do_algebra({"+", "*", "-"}, {2, 3, 4, 5}) == 9);
    assert (do_algebra({"//", "*"}, {7, 3, 4}) == 8);
}

