string longest(vector<string> strings) {
    if (strings.empty()) {
        return ""; // Return an empty string to represent None
    }

    string maxString = strings[0]; // Initialize with the first string

    for (const string& str : strings) {
        if (str.length() > maxString.length()) {
            maxString = str;
        }
    }

    return maxString;
}


