using namespace std;

vector<int> parse_music(string music_string) {
    vector<int> result;
    int i = 0;
    while (i < music_string.length()) {
        if (music_string[i] == 'o') {
            if (i + 1 < music_string.length() && music_string[i + 1] == '|') {
                // Half note
                result.push_back(2);
                i += 2;
            } else {
                // Whole note
                result.push_back(4);
                i += 1;
            }
        } else if (music_string[i] == '.') {
            // Quarter note
            result.push_back(1);
            i += 2;
        } else {
            // Skip spaces and other characters
            i += 1;
        }
    }
    return result;
}

