#include <stdio.h>
#include <string>
using namespace std;

int how_many_times(string str, string substring) {
    int count = 0;
    int n = str.length();
    int m = substring.length();

    // Iterate through the string
    for (int i = 0; i <= n - m; ++i) {
        // Check if the substring starting from position i matches the given substring
        if (str.substr(i, m) == substring) {
            count++;
        }
    }

    return count;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (how_many_times("", "x") == 0);
    assert (how_many_times("xyxyxyx", "x") == 4);
     assert (how_many_times("cacacacac", "cac") == 4);
    assert (how_many_times("john doe", "john") == 1);
}

