vector<float> rescale_to_unit(vector<float> numbers) {
    // Step 1: Find the minimum and maximum values in the vector
    float min_value = *min_element(numbers.begin(), numbers.end());
    float max_value = *max_element(numbers.begin(), numbers.end());

    // Step 2: Create a new vector to store the transformed values
    vector<float> transformed_numbers;

    // Step 3: Apply the transformation formula to each element
    for (float value : numbers) {
        float new_value = (value - min_value) / (max_value - min_value);
        transformed_numbers.push_back(new_value);
    }

    // Step 4: Return the new vector
    return transformed_numbers;
}

