using namespace std;

string sort_numbers(string numbers) {
    // Map to convert number words to integers
    map<string, int> number_map = {
        {"zero", 0}, {"one", 1}, {"two", 2}, {"three", 3},
        {"four", 4}, {"five", 5}, {"six", 6}, {"seven", 7},
        {"eight", 8}, {"nine", 9}
    };

    // Split the input string into a list of number words
    stringstream ss(numbers);
    vector<string> number_words;
    string word;
    while (ss >> word) {
        number_words.push_back(word);
    }

    // Convert number words to integers
    vector<int> numbers_int;
    for (const string& word : number_words) {
        numbers_int.push_back(number_map[word]);
    }

    // Sort the integers
    sort(numbers_int.begin(), numbers_int.end());

    // Convert sorted integers back to number words
    vector<string> sorted_number_words;
    for (int num : numbers_int) {
        for (const auto& pair : number_map) {
            if (pair.second == num) {
                sorted_number_words.push_back(pair.first);
                break;
            }
        }
    }

    // Join the sorted number words into a single string
    string result;
    for (size_t i = 0; i < sorted_number_words.size(); ++i) {
        result += sorted_number_words[i];
        if (i < sorted_number_words.size() - 1) {
            result += " ";
        }
    }

    return result;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (sort_numbers("") == "");
    assert (sort_numbers("three") == "three");
    assert (sort_numbers("three five nine") == "three five nine");
     assert (sort_numbers("five zero four seven nine eight") == "zero four five seven eight nine");
      assert (sort_numbers("six five four three two one zero") == "zero one two three four five six");
}

