vector<int> remove_duplicates(vector<int> numbers) {
    // Step 1: Initialize a hash map to store the frequency of each element
    unordered_map<int, int> frequency;

    // Step 2: Iterate through the input vector and update the frequency in the hash map
    for (int num : numbers) {
        frequency[num]++;
    }

    // Step 3: Initialize an empty result vector
    vector<int> result;

    // Step 4: Iterate through the input vector again and add elements to the result vector if their frequency is 1
    for (int num : numbers) {
        if (frequency[num] == 1) {
            result.push_back(num);
        }
    }

    // Step 5: Return the result vector
    return result;
}

