int largest_divisor(int n) {
    int largest_div = -1; // Initialize with -1 to indicate no divisor found
    for (int i = n - 1; i >= 1; --i) {
        if (n % i == 0) {
            largest_div = i;
            break;
        }
    }
    return largest_div;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (largest_divisor(3) == 1);
    assert (largest_divisor(7) == 1);
    assert (largest_divisor(10) == 5);
    assert (largest_divisor(100) == 50);
    assert (largest_divisor(49) == 7);
}

