vector<int> factorize(int n) {
    vector<int> factors;
    // Check for divisibility by 2
    while (n % 2 == 0) {
        factors.push_back(2);
        n /= 2;
    }
    // Check for divisibility by odd numbers starting from 3
    for (int i = 3; i <= sqrt(n); i += 2) {
        while (n % i == 0) {
            factors.push_back(i);
            n /= i;
        }
    }
    // If n is a prime number greater than 2
    if (n > 2) {
        factors.push_back(n);
    }
    return factors;
}

// Function to test the factorize function
void test_factorize() {
    vector<int> result;
    result = factorize(8);
    for (int factor : result) printf("%d ", factor); printf("\n"); // Output: 2 2 2

    result = factorize(25);
    for (int factor : result) printf("%d ", factor); printf("\n"); // Output: 5 5

    result = factorize(70);
    for (int factor : result) printf("%d ", factor); printf("\n"); // Output: 2 5 7
}


