using namespace std;

string flip_case(string str) {
    string result = "";
    for (char ch : str) {
        if (islower(ch)) {
            result += toupper(ch);
        } else if (isupper(ch)) {
            result += tolower(ch);
        } else {
            result += ch; // Non-alphabetic characters remain unchanged
        }
    }
    return result;
}

