vector<int> sort_third(vector<int> l) {
    // Step 1: Identify indices divisible by three
    vector<int> divisible_indices;
    for (int i = 0; i < l.size(); ++i) {
        if (i % 3 == 0) {
            divisible_indices.push_back(i);
        }
    }

    // Step 2: Extract values at these indices
    vector<int> values_to_sort;
    for (int index : divisible_indices) {
        values_to_sort.push_back(l[index]);
    }

    // Step 3: Sort these values
    sort(values_to_sort.begin(), values_to_sort.end());

    // Step 4: Replace values at the identified indices with the sorted values
    for (int i = 0; i < divisible_indices.size(); ++i) {
        l[divisible_indices[i]] = values_to_sort[i];
    }

    // Step 5: Return the modified vector
    return l;
}

