vector<float> sort_even(vector<float> l) {
    vector<float> even_indices_elements;

    // Extract elements at even indices
    for (int i = 0; i < l.size(); i++) {
        if (i % 2 == 0) {
            even_indices_elements.push_back(l[i]);
        }
    }

    // Sort the extracted elements
    sort(even_indices_elements.begin(), even_indices_elements.end());

    // Create the result vector
    vector<float> result(l.size());
    int even_index = 0;

    // Place sorted elements at even indices and original elements at odd indices
    for (int i = 0; i < l.size(); i++) {
        if (i % 2 == 0) {
            result[i] = even_indices_elements[even_index++];
        } else {
            result[i] = l[i];
        }
    }

    return result;
}

