float median(vector<float> l) {
    // Sort the vector
    sort(l.begin(), l.end());

    int n = l.size();

    // Check if the number of elements is odd
    if (n % 2 == 1) {
        // Return the middle element
        return l[n / 2];
    } else {
        // Return the average of the two middle elements
        return (l[n / 2 - 1] + l[n / 2]) / 2.0;
    }
}

