int largest_prime_factor(int n) {
    int largest_factor = 1;

    // Remove factors of 2
    while (n % 2 == 0) {
        largest_factor = 2;
        n /= 2;
    }

    // Check for odd factors
    for (int i = 3; i <= sqrt(n); i += 2) {
        while (n % i == 0) {
            largest_factor = i;
            n /= i;
        }
    }

    // If n is a prime number greater than 2
    if (n > 2) {
        largest_factor = n;
    }

    return largest_factor;
}


